exports.initialize = function () {
	var self = this;
	var temperature;
	var clock = 0;
	var step = 0;

	this.addInputHandler('inTemperature', function () {
		self.temperature = self.get('inTemperature');
	});
	this.addInputHandler('anomaly', function () {
		clock += 1;
		self.send('display', {"clock": clock, 'step': step});
	});

	this.addInputHandler('input', function () {

		if (self.temperature == null)
			self.temperature = self.get('inTemperature');

//		if (parseInt(clock) >= 400 && parseInt(clock) % 23 == 0 && parseInt(step) < 5){
//			clock -= 1;
//			self.send('output', null);
//			step += 1;
//			}
//		else{
//			step = 0;
		self.send('output', {'temperature':self.temperature,
								 	 'id': '2',
									 'Source': 'Thermostat',
								 	 'Destination': 'Controller'});
//			}
	});
};

