exports.initialize = function () {
	var self = this;
	var heaterOn = false;
	var clock = 0;
	var step = 0;
	var electriciy = 45

	this.addInputHandler('ControllerCommand4SmartHeater', function () {
		var ControllerCmd = self.get('ControllerCommand4SmartHeater');
		clock += 1;
		self.send('display', {'clock':clock, 'step': step});
		if (ControllerCmd.Command == null)
			return;

		if ((ControllerCmd.Command == "heaterOn")){

//			if (parseInt(clock) >= 150 && parseInt(clock) % 20 == 0 && parseInt(step) < 5) {
//				clock -= 1;
//				self.send('output', {"Command":"SmartheaterOff",
//									 "id": "13",
//									 "Source": "SamrtHeater",
//									 "Destination": "Controller"
//									});
//				heaterOn = false;
//				step += 1;
//			}
//				
//			
//			else {
			self.send('output', {"Command":"SmartheaterOn",
									"id": "13",
									 "Source": "SamrtHeater",
									 "Destination": "Controller"});
			self.send('ElectriciylevelOfHeater', electriciy + Math.floor(Math.random() * 5));
			heaterOn = true;
//				step = 0;
//			}
//	

		} else if (ControllerCmd.Command == "heaterOff" && heaterOn == true) {
			self.send('output', {"Command":"SmartheaterOff",
								"id": "13",
								"Source": "SamrtHeater",
								"Destination": "Controller"});
			heaterOn = false;
			self.send('ElectriciylevelOfHeater', 0);

		}
	});
}

