	this.output('brightness', {'type':'int'});
};

//var http = require('@accessors-modules/http-client');
exports.initialize = function () {
	var self = this;
	var state = 0;
	var duration = 5;
	var brightness = 20;

	this.addInputHandler('clock', function () {
		switch (state) {
			case 0:
				brightness += 20;
				if (--duration == 0) {
					state = 1;
					duration = 50;
				}
				break;
			case 1:
				if (--duration == 0) {
					state = 2;
					duration = 10;
				}
				break;
			case 2:
				brightness -= 10;
				if (--duration == 0) {
					state = 3;
					duration = 50;
				}
				break;
			case 3:
				if (--duration == 0) {
					state = 0;
					duration = 5;
				}
				break;
		}
		self.send('brightness', (brightness + Math.floor(Math.random() * 5) - 2));
	});
};

