exports.setup = function () {
	this.input('input');
	this.input('lightSensorData', {'type':'JSON'});
	this.input('thermostatData', {'type':'JSON'});
	this.input('ElectricityLevelOfLights', {'type':'JSON'});
	this.input('ElectricityLevelOfHeaters', {'type':'JSON'});


	this.output('command4SmartLightBulb', {'type':'JSON'});
	this.output('command4SmartHeater', {'type':'JSON'});
	this.output('ask4ThermostatData', {'type':'JSON'});
	this.output('ask4LightSensorData', {'type':'JSON'});


};


exports.initialize = function () {
	var self = this;
	var lightOn = false;
	var heaterOn = false;
	var electricityOn = false;


	this.addInputHandler('input', function () {
		self.send('ask4ThermostatData', {"Command": "getTempData",
										 "id": "10",
										 "Source": "Controller",
										 "Destination": "Thermostat"}); 

		self.send('ask4LightSensorData', {"Command":"getBrightnessData",
										  "id": "10",
										  "Source": "Controller",
										  "Destination": "LightSensor"});

	});


	// ------------------------Controlling Smart light-------------------------------------
	// Data from Light Sensor 1
	this.addInputHandler('lightSensorData', function () {
		var data = self.get('lightSensorData');
		if (data.brightnessLevel == null) {
			self.send('command4SmartLightBulb', {"Command":"lightOn",
												 "id": "10",
												 "Source": "Controller",
												 "Destination": "ElectricityPower"});
			lightOn = true;
		}

		else if (data.brightnessLevel < 80) {
			self.send('command4SmartLightBulb', {"Command":"lightOn",
												 "id": "10",
												 "Source": "Controller",
												 "Destination": "ElectricityPower"});
			lightOn = true;

		} else if (data.brightnessLevel > 140 && lightOn == true) {
			self.send('command4SmartLightBulb', {"Command":"lightOff",
												 "id": "10",
												 "Source": "Controller",
												 "Destination": "ElectricityPower"});
			lightOn = false;
		}

	});


	// -------------------------------Controlling Smart Heater----------------------------
	// Data from Thermostat 1
	this.addInputHandler('thermostatData', function () {
		var data = self.get('thermostatData');
		if (data  == null){
			self.send('command4SmartHeater', {"Command":"heaterOn",
											  "id": "10",
											  "Source": "Controller",
											  "Destination": "Heater"}); 
			heaterOn = true;
		}

		else if (data.temperature >= 55 && heaterOn == true) {
			self.send('command4SmartHeater', {"Command":"heaterOff",
											  "id": "10",
											  "Source": "Controller",
											  "Destination": "Heater"}); 
			heaterOn = false;
		} else if (data.temperature <= 20) {
			self.send('command4SmartHeater', {"Command":"heaterOn",
											  "id": "10",
											  "Source": "Controller",
											  "Destination": "Heater"}); 
			heaterOn = true;
		}
	});

	// ------------------------Controlling Electiricity lights-------------------------------------
	// Data from Light Sensor 1
	this.addInputHandler('ElectricityLevelOfLights', function () {
		var data = self.get('ElectricityLevelOfLights');

	});


};
